create table account_subject (id bigint not null auto_increment, code varchar(255) not null, english_name varchar(255), entry_type enum ('CREDIT','DEBIT') not null, increase_decrease_type enum ('DECREASE','INCREASE') not null, is_active bit not null, is_business_car bit not null, is_foreign_currency bit not null, modification_type bit not null, name varchar(255) not null, nature_code varchar(255), standard_financial_statement_code varchar(255), parent_code varchar(255), structure_code varchar(255) not null, primary key (id)) engine=InnoDB;
create table account_subject_cash_memo (id bigint not null auto_increment, code varchar(255), content varchar(255) not null, account_subject_code varchar(255), primary key (id)) engine=InnoDB;
create table account_subject_fixed_memo (id bigint not null auto_increment, category varchar(255) not null, code varchar(255), content varchar(255) not null, account_subject_code varchar(255), primary key (id)) engine=InnoDB;
create table account_subject_nature (id bigint not null auto_increment, code varchar(255) not null, name varchar(255) not null, structure_id bigint not null, primary key (id)) engine=InnoDB;
create table account_subject_standard_financial_statement (id bigint not null auto_increment, code varchar(255) not null, increase_decrease_type enum ('DECREASE','INCREASE') not null, name varchar(255) not null, structure_code varchar(255), primary key (id)) engine=InnoDB;
create table account_subject_structure (id bigint not null auto_increment, code varchar(255) not null, max integer not null, min integer not null, name varchar(255) not null, primary key (id)) engine=InnoDB;
create table account_subject_transfer_memo (id bigint not null auto_increment, code varchar(255), content varchar(255) not null, account_subject_code varchar(255), primary key (id)) engine=InnoDB;
create table attendance_management (id bigint not null auto_increment, attendance_code varchar(255) not null, check_time time(6) not null, checkout_time time(6) not null, date date not null, status enum ('ABSENT','BUSINESS_TRIP','EARLY_LEAVE','LATE','LEAVE','ON_DUTY','OVERTIME','PRESENT','PUBLIC_HOLIDAY','REMOTE_WORK','SABBATICAL','SHIFT_WORK','SICK_LEAVE','TRAINING') not null, employee_id bigint not null, primary key (id)) engine=InnoDB;
create table attendance_overtime (id bigint not null auto_increment, hours datetime(6) not null, overtime_date date not null, employee_id bigint not null, primary key (id)) engine=InnoDB;
create table bank_account_type (id bigint not null auto_increment, code varchar(255), type_name varchar(255) not null, primary key (id)) engine=InnoDB;
create table basic_data_lot (id bigint not null auto_increment, inventory varchar(255) not null, lot_number varchar(255) not null, product varchar(255) not null, remarks varchar(255), production_receipt_id bigint not null, previous_lot_id bigint, primary key (id)) engine=InnoDB;
